mapper.update:
		update
					 work_log
			 set 
			     wl_clock_out = #{clockOutTime}
			   , wl_status = #{status}
			   , wl_time = #{workTime}
		 where mem_no = #{memNo}
  		 and trunc(wl_work_date) = trunc(sysdate)

mapper.select:
select WL_CLOCK_IN, WL_CLOCK_OUT, WL_STATUS, WL_WORK_DATE from WORK_LOG
MEM_NO = #{memNo}
AND TO_CHAR(WL_WORK_DATE, 'YY') = #{year} AND TO_CHAR(WL_WORK_DATE, 'MM') = #{month}
order by WL_WORK_DATE DESC
